import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { fetchImages, setCategory, setSelectedImage, clearSelectedImage } from './store/gallerySlice';
import Header from './components/Header';
import GalleryGrid from './components/GalleryGrid';
import StatusMessage from './components/StatusMessage';
import CategoryModal from './components/CategoryModal';
import ImageModal from './components/ImageModal';

const AppContainer = styled.div`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const dispatch = useDispatch();
  const { images, currentPage, category, sortBy, loading, error, selectedImage } = useSelector(
    (state) => state.gallery
  );
  const [showCategoryModal, setShowCategoryModal] = React.useState(false);

  useEffect(() => {
    dispatch(fetchImages({ category, page: currentPage, sortBy }));
  }, [dispatch, category, currentPage, sortBy]);

  const handlePrevPage = () => {
    if (currentPage > 1) {
      dispatch(fetchImages({ category, page: currentPage - 1, sortBy }));
    }
  };

  const handleNextPage = () => {
    dispatch(fetchImages({ category, page: currentPage + 1, sortBy }));
  };

  const handleImageClick = (image) => {
    dispatch(setSelectedImage(image));
  };

  const handleCategorySelect = (newCategory) => {
    dispatch(setCategory(newCategory));
    setShowCategoryModal(false);
  };

  if (loading) return <StatusMessage type="loading" message="Loading..." />;
  if (error) return <StatusMessage type="error" message={`Error: ${error}`} />;

  return (
    <AppContainer>
      <Header
        onPrevPage={handlePrevPage}
        onNextPage={handleNextPage}
        onCategoryClick={() => setShowCategoryModal(true)}
      />

      <GalleryGrid
        images={images}
        onImageClick={handleImageClick}
      />

      {showCategoryModal && (
        <CategoryModal
          onClose={() => setShowCategoryModal(false)}
          onSelect={handleCategorySelect}
        />
      )}

      {selectedImage && (
        <ImageModal
          image={selectedImage}
          onClose={() => dispatch(clearSelectedImage())}
        />
      )}
    </AppContainer>
  );
}

export default App;
